    private void jMenuItem7ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem7ActionPerformed
        int Data1 = Integer.parseInt(eData1.getText()); // Dianggap sebagai panjang
        int Data2 = Integer.parseInt(eData2.getText()); // Dianggap sebagai lebar
        int Hasil;
        Hasil = Data1 * Data2; // Menghitung luas persegi panjang
        eHasil.setText(String.valueOf(Hasil));
    }//GEN-LAST:event_jMenuItem7ActionPerformed

    private void jMenuItem5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem5ActionPerformed
        int Data1 = Integer.parseInt(eData1.getText()); // Dianggap sebagai jari-jari lingkaran
        int Hasil;
        Hasil = (int) (Math.PI * Data1 * Data1); // Menghitung luas lingkaran
        eHasil.setText(String.valueOf(Hasil));
    }//GEN-LAST:event_jMenuItem5ActionPerformed

    private void jMenuItem6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem6ActionPerformed
        int Data1 = Integer.parseInt(eData1.getText()); // Dianggap sebagai jari-jari lingkaran
        int Hasil;
        Hasil = (int) (2 * Math.PI * Data1); // Menghitung keliling lingkaran
        eHasil.setText(String.valueOf(Hasil));
    }//GEN-LAST:event_jMenuItem6ActionPerformed

    private void jMenuItem1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem1ActionPerformed
        int Data1 = Integer.parseInt(eData1.getText()); // Dianggap sebagai panjang
        int Data2 = Integer.parseInt(eData2.getText()); // Dianggap sebagai lebar
        int Hasil;
        Hasil = 2 * (Data1 + Data2); // Menghitung keliling persegi panjang
        eHasil.setText(String.valueOf(Hasil));
    }//GEN-LAST:event_jMenuItem1ActionPerformed

    private void jMenuItem3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem3ActionPerformed
        int Data1 = Integer.parseInt(eData1.getText()); // Dianggap sebagai alas
        int Data2 = Integer.parseInt(eData2.getText()); // Dianggap sebagai tinggi
        int Hasil;
        Hasil = (int) (0.5 * Data1 * Data2); // Menghitung luas segitiga
        eHasil.setText(String.valueOf(Hasil));

    }//GEN-LAST:event_jMenuItem3ActionPerformed

