    private void jMenuItem4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem4ActionPerformed
        int Data1 = Integer.parseInt(eData1.getText()); // Dianggap sebagai alas
        int Data2 = Integer.parseInt(eData2.getText()); // Dianggap sebagai sisi kedua
        int Data3 = Integer.parseInt(eData3.getText()); // Dianggap sebagai sisi ketiga
        int Hasil;
        Hasil = Data1 + Data2 + Data3; // Menghitung keliling segitiga
        eHasil.setText(String.valueOf(Hasil));

    }//GEN-LAST:event_jMenuItem4ActionPerformed

